return $('Aggregate1').all().map(item => {
  // Tenta acessar a propriedade 'conversas' e verifica se ela é um array
  const conversas = item.json.conversas || []; // Garante que é pelo menos um array vazio

  if (!Array.isArray(conversas)) {
    throw new Error("A propriedade 'conversas' não é um array.");
  }

  // Processa cada conversa e verifica as mensagens
  const textoUnico = conversas.map(conversa => {
    const cliente = conversa.user_message || "sem mensagem do chatbot";
    const agente = conversa.bot_message || "sem resposta";

    // Verifica se a data existe e formata
    const dataOriginal = conversa.created_at || "Data da Mensagem indisponível";
    const dataFormatada = dataOriginal !== "Data da Mensagem indisponível"
      ? formatarData(dataOriginal)
      : dataOriginal;

    return `em: ${dataFormatada}\n\n - agente(chatbot): ${agente} \n - cliente: ${cliente}\n`;
  }).join('\n\n');

  // Retorna o texto final como resultado
  return {
    json: {
      texto: textoUnico
    }
  };
});

// Função para formatar a data
function formatarData(dataString) {
  const data = new Date(dataString); // Converte a string em objeto Date
  if (isNaN(data)) {
    return "Data inválida"; // Retorna se a data não for válida
  }

  // Formata no padrão DD/MM/YYYY HH:mm
  const dia = String(data.getDate()).padStart(2, '0');
  const mes = String(data.getMonth() + 1).padStart(2, '0'); // Mês começa em 0
  const ano = data.getFullYear();
  const horas = String(data.getHours()).padStart(2, '0');
  const minutos = String(data.getMinutes()).padStart(2, '0');

  return `${dia}/${mes}/${ano} ${horas}:${minutos}`;
}

